frame_eqs_eel:
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.91+1.3);

wave_r = 0.5 + 0.5*(0.6*sin(time*1.3) + 0.4*sin(0.98*time));
wave_b = 0.5 + 0.5*(0.6*sin(time*1.1) + 0.4*sin(0.78*time));
wave_g = 0.5 + 0.5*(0.6*sin(time*1.2) + 0.4*sin(0.6*time));

q8=wave_r;
q7=wave_b;
q6=wave_g;

wr = 0.5+0.4*(0.6*sin(time*0.2) + 0.4*sin(0.8*time));
wb = 0.5+0.4*(0.6*sin(time*0.377) + 0.4*sin(0.5*time));
wg = 0.5+0.4*(0.6*sin(time*0.7) + 0.4*sin(0.4*time));

q10=wr;//*above(bass_thresh, 1.9)*1;
q11=wb;//*above(bass_thresh, 1.9)*1;
q12=wg;//*above(bass_thresh, 1.9)*1;

q10=0.8;
q11=0.2;
q12=0.1;


q18 = 0.01*sin(mtime*0.1);
q17 = -0.01*sin(mtime*0.254);
q2=bass_thresh;

vol=(bass+mid+treb)*0.25;
vol=vol*vol;
q3=vol;
q5=vol*0.5;
mtime=mtime+vol*0.01;
q2=mtime*0.25;

warp:
shader_body
{

    vec3 rc = ((texture(sampler_blur1, uv).xyz * scale1) + bias1)+texture(sampler_main, uv).xyz;

    vec2 ruv = uv = 0.5 + (uv-0.5)*(1+(rc.y*0.05));  

    ruv = fract(vec2(uv.x+pow(rc.x,q17), uv.y+pow(rc.x, q18)));


    ret = texture(sampler_fc_main, vec2(ruv.x, ruv.y)).xyz;

    vec3 noise3 = mix(vec3(0,0,0),vec3(q10,q11,q12), (rad));    
    vec3 noise2 = texture(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy).xyz;
    noise2 += noise3*vol;

    ret += 0.3*clamp(cross((1-ret), (noise2)), 0.0, 1.0);

    ret *= 0.97; 


}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;
    ret *= vec3(0.9, 0.3, 0.5);

    ret *= 1.34; //gamma
    ret *= ret; //darken
}

warp_hlsl:
shader_body
{

    float3 rc = GetBlur1(uv)+GetPixel(uv);

    float2 ruv = uv = 0.5 + (uv-0.5)*(1+(rc.y*0.05));  

    ruv = frac(float2(uv.x+pow(rc.x,q17), uv.y+pow(rc.x, q18)));


    ret = tex2D(sampler_fc_main, float2(ruv.x, ruv.y)).xyz;

    float3 noise3 = lerp(float3(0,0,0),float3(q10,q11,q12), (rad));    
    float3 noise2 = tex2D(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy);
    noise2 += noise3*vol;

    ret += 0.3*saturate(cross((1-ret), (noise2)));

    ret *= 0.97; 


}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret *= float3(0.9, 0.3, 0.5);

    ret *= 1.34; //gamma
    ret *= ret; //darken
}
