vec3 maxNeumann(vec2 domain){
   vec3 maxneumann = vec3(0,0,0);
   vec2 pixel = texsize.zw;
   maxneumann = max(maxneumann, texture(sampler_fc_main, domain + pixel*vec2(-1, 0) ).xyz);
   maxneumann = max(maxneumann, texture(sampler_fc_main, domain + pixel*vec2( 0,-1) ).xyz);
   maxneumann = max(maxneumann, texture(sampler_fc_main, domain + pixel*vec2( 0, 0) ).xyz);
   maxneumann = max(maxneumann, texture(sampler_fc_main, domain + pixel*vec2( 0, 1) ).xyz);
   maxneumann = max(maxneumann, texture(sampler_fc_main, domain + pixel*vec2( 1, 0) ).xyz);
   return maxneumann;
}



