{

   float pixelDistance   = 4;
   float motionStrength  = 4;

   float diffusionFactor = 1;  
   float decay           = -0.01 + texture(sampler_main, uv).xyz.x*0;

   vec2 uv_y = mix(uv_orig,uv,1);
   vec2 dither_uv = uv*texsize.xy*texsize_noise_lq.zw*diffusionFactor;

   vec2 d = texsize.zw*pixelDistance;
   float dx = ( 2*((texture(sampler_blur1, uv_y + vec2(1,0)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, uv_y-vec2(1,0)*d).xyz * scale1) + bias1) ).y*0.5;
   float dy = ( 2*((texture(sampler_blur1, uv_y + vec2(0,1)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, uv_y-vec2(0,1)*d).xyz * scale1) + bias1) ).y*0.5;

   vec2 my_uv = uv_y + vec2(dx,dy)*texsize.zw*motionStrength;

   ret.y = texture( sampler_fw_main, clamp(my_uv, 0.0, 1.0)).y;

   ret.y += (ret - ((texture(sampler_blur1, uv_y).xyz * scale1) + bias1)).y*0.025 + decay;


   ret.y += (texture(sampler_noise_lq, dither_uv).y-0.5)*0.02;



   dx = ( 2*((texture(sampler_blur1, uv_y + vec2(1,0)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, uv_y-vec2(1,0)*d).xyz * scale1) + bias1) ).z*0.5;
   dy = ( 2*((texture(sampler_blur1, uv_y + vec2(0,1)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, uv_y-vec2(0,1)*d).xyz * scale1) + bias1) ).z*0.5;

   my_uv = uv_y - vec2(dx,dy)*texsize.zw*motionStrength;

   ret.z = texture(sampler_main, my_uv).xyz.z - ret.y*0.01 + 0.004;    
   ret.z += (texture(sampler_noise_lq, dither_uv).y-0.5)*0.01;




   dx = (2*((texture(sampler_blur1, uv+d*vec2(1,0)).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv+d*vec2(-1,0)).xyz * scale1) + bias1) ).x*0.5;
   dy = (2*((texture(sampler_blur1, uv+d*vec2(0,1)).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv+d*vec2(0,-1)).xyz * scale1) + bias1) ).x*0.5;

   vec2 d_red = +vec2(dx,dy)*texsize.zw;

   ret.x = maxNeumann(uv-d_red*2.5).x + (texture(sampler_main, uv+d_red*4).xyz.x - ((texture(sampler_blur1, uv + d_red*4).xyz * scale1) + bias1).x)*0.206 - 0.09;

