warp:
shader_body
{
    vec3 d = vec3(texsize.zw, 0) * 4;
    vec4 lums = 0;
    lums.x = lum( ((texture(sampler_blur1, uv + texsize.zw*d.xz).xyz * scale1) + bias1) );
    lums.y = lum( ((texture(sampler_blur1, uv - texsize.zw*d.xz).xyz * scale1) + bias1) );
    lums.z = lum( ((texture(sampler_blur1, uv + texsize.zw*d.zy).xyz * scale1) + bias1) );
    lums.w = lum( ((texture(sampler_blur1, uv - texsize.zw*d.zy).xyz * scale1) + bias1) );

    vec2 grad = vec2(lums.x-lums.y, lums.z-lums.w)*300;

    // sample previous frame
    ret = texture( sampler_fc_main, uv_orig + grad*texsize.zw ).xyz;

    // darken (decay) over time
    ret *= 0.98; //or try: ret -= 0.004;
}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;
    ret = ret*1.5 - 0.1;

    vec4 g;
    vec3 d = vec3(texsize.zw * 4, 0);
