warp:
shader_body
{
    // sample previous frame
    ret = texture( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, texture( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, texture( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

comp:
float a,b,c;
shader_body
{
uv *=2 ;
vec2 m = floor(fract(uv*0.5)*2);
uv = fract(uv)*(1-m) + (m)*fract(1-uv);

uv = 0.5 + (uv-0.5)*vec2(0.75,-0.9) - vec2(0,0.05);
    vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz + 0.04;
a =base.x/4;b = base.y/4; c = base.z/4;
c = c + b - c*b;
a = a + c - a*c;
base = 1.4*a;
ret = base*(1-base)*(1-base)*2*vec3(2,1.4,0) + pow(base,2)*vec3(-0.3,0.2,0.6);
//ret.xy = ret.yx;
ret /= vec3(0.6,-2.1,2.0);
ret /= vec3(0.9,-2.4,2.4);
}

