warp:
shader_body
{

    // sample previous frame
    ret.xyz = texture( sampler_main, uv ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      ret.xyz += (ret - ((texture(sampler_blur3, uv).xyz * scale3) + bias3))*0.35;
      ret.xyz -= 0.004;

      // add noise:
      vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
      ret.xyz += (texture(sampler_noise_lq, dither_uv).xyz-0.5)*0.18;

      // desaturate over time, to keep the globs white
      ret.xyz = mix(ret.xyz, lum(ret.xyz), 0.2);
}

comp:
float a,b,c;
shader_body
{
    vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz*0.8 - 0.4 ;
a =base.x/4;b = base.y/4; c = base.z/4;
c = c + b - c*b;
a = a + c - a*c;
base = 1.4*a;
ret = base*(1-base)*(1-base)*4*vec3(1,0.45,-0.4) + pow(base,2)*vec3(0.1,0.2,0.3);
//ret.xy = ret.yx;
}

warp_hlsl:
shader_body
{

    // sample previous frame
    ret.xyz = tex2D( sampler_main, uv ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      ret.xyz += (ret - GetBlur3(uv))*0.35;
      ret.xyz -= 0.004;

      // add noise:
      float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
      ret.xyz += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)*0.18;

      // desaturate over time, to keep the globs white
      ret.xyz = lerp(ret.xyz, lum(ret.xyz), 0.2);
}

