warp:
shader_body
{

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
vec3 rand = texture(sampler_noise_lq, dither_uv).xyz-0.5;

vec2 d = texsize.zw * 4;
vec3 dx = ( ((texture(sampler_blur1, uv + vec2(.8,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv + vec2(2,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
d = mix(uv_orig,uv,0.25);
vec2 uv_y =d - vec2(dx.y,dy.y)*texsize.zw*4;
vec2 uv_z =d - vec2(dx.z,dy.z)*texsize.zw*4;

ret.y = texture(sampler_main, uv_y).xyz.y - (((texture(sampler_blur1, uv).xyz * scale1) + bias1).y-texture(sampler_main, uv_y).xyz.y)*0.024 - 0.009 + (1-texture(sampler_main, uv_orig).xyz.x)*0.014 + rand*0.02;
ret.z = texture(sampler_main, uv_z).xyz.z - (((texture(sampler_blur1, uv).xyz * scale1) + bias1).z-texture(sampler_main, uv_z).xyz.z)*0.024 - 0.009 + (0+texture(sampler_main, uv_orig).xyz.x)*0.014 + rand*0.02;

ret.x = texture( sampler_fc_main, uv+rand*texsize.zw*.5 ).x;
ret.x += (ret.x - ((texture(sampler_blur3, uv).xyz * scale3) + bias3).x)*0.4 + rand*0.0052;

//ret =vec3(0);
}

comp:
float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body
{
    vec2 d = texsize.zw*4;
    vec3 dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

vec2 uv_x = uv - vec2(dx.y,dy.y)*0.1 + vec2(dx.z,dy.z)*0.06;
ret =((texture(sampler_blur2, uv_x).xyz * scale2) + bias2).x*clamp(1-texture(sampler_main, uv).xyz.y, 0.0, 1.0)*_pow(hue_shader.yxz,8)*3;
ret =mix(ret,_pow(hue_shader.yzx,8)*1.4,texture(sampler_main, uv_x).xyz.x*0.8 + ((texture(sampler_blur1, uv_x).xyz * scale1) + bias1).x);
ret *= clamp(1 - ((texture(sampler_blur1, uv).xyz * scale1) + bias1).z*4, 0.0, 1.0);
ret =mix(ret,1,_pow(hue_shader,8)*texture(sampler_main, clamp(uv - vec2(dx.z,dy.z)*2, 0.0, 1.0)).xyz.y*1.2);
ret =mix(ret,_pow(hue_shader,8).zxy*1.8,texture(sampler_main, uv).xyz.z);
ret /= vec3(0.7,-2.7,-1.5);
}

warp_hlsl:
shader_body
{

float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
float3 rand = tex2D(sampler_noise_lq, dither_uv).xyz-0.5;

float2 d = texsize.zw * 4;
float3 dx = ( GetBlur1(uv + float2(.8,0)*d) - GetBlur1(uv-float2(1,0)*d) );
float3 dy = ( GetBlur1(uv + float2(2,1)*d) - GetBlur1(uv-float2(0,1)*d) );
d = lerp(uv_orig,uv,0.25);
float2 uv_y =d - float2(dx.y,dy.y)*texsize.zw*4;
float2 uv_z =d - float2(dx.z,dy.z)*texsize.zw*4;

ret.y = GetPixel(uv_y).y - (GetBlur1(uv).y-GetPixel(uv_y).y)*0.024 - 0.009 + (1-GetPixel(uv_orig).x)*0.014 + rand*0.02;
ret.z = GetPixel(uv_z).z - (GetBlur1(uv).z-GetPixel(uv_z).z)*0.024 - 0.009 + (0+GetPixel(uv_orig).x)*0.014 + rand*0.02;

ret.x = tex2D( sampler_fc_main, uv+rand*texsize.zw*.5 ).x;
ret.x += (ret.x - GetBlur3(uv).x)*0.4 + rand*0.0052;

//ret = 0;
}

comp_hlsl:
shader_body
{
    float2 d = texsize.zw*4;
    float3 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) );
    float3 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) );

float2 uv_x = uv - float2(dx.y,dy.y)*0.1 + float2(dx.z,dy.z)*0.06;
ret = GetBlur2(uv_x).x*saturate(1-GetPixel(uv).y)*pow(hue_shader.yxz,8)*3;
ret = lerp(ret,pow(hue_shader.yzx,8)*1.4,GetPixel(uv_x).x*0.8 + GetBlur1(uv_x).x);
ret *= saturate(1 - GetBlur1(uv).z*4);
ret = lerp(ret,1,pow(hue_shader,8)*GetPixel(saturate(uv - float2(dx.z,dy.z)*2)).y*1.2);
ret = lerp(ret,pow(hue_shader,8).zxy*1.8,GetPixel(uv).z);
ret /= float3(0.7,-2.7,-1.5);
}
