frame_eqs_eel:
dec_med = pow (0.5, 30/fps);
dec_slow = pow (0.9, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;
index2 = (index2 + is_beat*bnot(index))%2;
index3 = (index3 + is_beat*bnot(index)*bnot(index2))%3;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att ;
q27 = index + 1;
q28 = -index2/100;
q29 = bnot(below(index2+index3,1));

puls = dec_slow*puls + (1-dec_slow)*q29;
q32 = puls;

k1 =  is_beat*equal(index%2,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/4;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

trel1 = trel1 + 4.2*q1/fps*(q22);
trel2 = trel2 + 3*q2/fps*(1);
px = .2*sin(trel1);
py = .1*cos(trel2);
q5 = px;
q6 = py;

rotx = rotx + q5/fps*4;
roty = roty + q6/fps*4;
q15 = rotx;
q16 = roty;

ppx = ppx*dec_slow + (1-dec_slow)*px;
ppy = ppy*dec_slow + (1-dec_slow)*py;
q7 = ppx;
q8 = ppy;


q10 = 20; //speed shot
q11 = .8; //brightness shot


zoom = 1;
dy = .00;
dx = .0;
warp = .1;

