frame_eqs_eel:
dec_med = pow (0.8, 30/fps);
dec_slow = pow (0.9, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%5;
monitor = index2;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;

sb = sb*dec_med + q21*(1-dec_med);
q29 = sb;

k1 =  is_beat*bnot(index)*bnot(index2);
p1 =  (index2-2);

p2 = dec_med * p2+ (1-dec_med)*p1;
p3 = dec_med * p3+ (1-dec_med)*p2;
q5 = cos(p3*3.14/2);

rott =  rott +  .003*30/fps*p3;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

movx = movx + .002*30/fps;
q28 = movx;

q15 = (1+sin(time/23))*.15;
q29 = 4*(.5+sin(time/17));
q30 = 5/(1.2+sin(time/50)); 
q31 = sin(time/38)/3;
q32 = 2+sin(time/18);

warp = .06;
zoom = .998;

