frame_eqs_eel:
dec_med = pow (0.96, 30/fps);
dec_slow = pow (0.98, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .5+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;
index2 = (index2 + is_beat*bnot(index))%2;
index3 = (index3 + is_beat*bnot(index) * bnot(index2))%2;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;

vol = bass_att + treb_att;
v2 = v2 * dec_slow + vol * (1-dec_slow) ;
q26 = max(atan (vol - v2*.8),.3);

q27 = index + 3;

sw = sw*dec_med + (1-dec_med)*(index2%2);
q28 = sw;

k1 =  is_beat*bnot(index);
k2 =  is_beat*bnot(index);

p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_slow * p2+ (1-dec_slow)*p1;
rott = p2 * 3.1416/2;
monitor = k1;
q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;


q5 = cos(time/6);
q6 = -sin(time/6);
q7 = -q6;
q8 = q5;

zoom = 1;
rot = -0;

movez = movez + .0035*(q1+1.1)*30/fps;
q29 = movez;
rota = rota + .001*(2-q1)*30/fps;
q30 = rota;
q31 = 1 + 16*abs(cos(time*.1));
q32 = .5*cos(time*.08)/4;

warp:
vec3 mus;
vec2 rs, uv1, uv6, uv7, uv8;
float dx,dy;
shader_body {
uv = uv_orig;
uv1 = (uv-.2) * aspect.xy;

//BACKGROUND
float z = 16*length(uv1)+time;
vec2 uvn =normalize(uv1);
rs = clamp(tan(z)*uvn,-5,5)/2;

//CRYSTAL
float mult = q31;
uv7 = sin(uv1*mult);
uv8 = uv7;
float dist = length(uv8);
dist *= (abs(uv1.x) + abs(uv1.y))+ q32;

float ang2 = atan (uv8.y,uv8.x);
ang2 =  .1*floor (16* ang2);
vec2 dir = .5*uv8+vec2 (cos(ang2), sin(ang2));


z = (1-cos(2*dist));
z = clamp(z, 0.0, 1.0);

//ADD
float mask = (z >= .5); //momentan beliebig
rs = q28*rs + (1-q28)*rs.yx;
uv += (1-mask)*.03*(rs.yx) + .5*mask*z*normalize(dir);

//ZENTRALSTRAHL
uv6 = (mat2(_qb) * uv1);
mus = abs(0.01/(sqrt(uv6.x)+.001))*(rad+.2);

//DOTS
vec2 uv2 = .7*uv1+.3*sin(uv1*16+4*vec2 (q5,q6));
uv2 *= .2;
vec3 dots = q26*clamp(.001/(length(uv2)), 0.0, 1.0);
vec3 blur = ((texture(sampler_blur1, fract(uv)).xyz * scale1) + bias1);
vec3 crisp= texture(sampler_main, uv).xyz;
vec3 diff = (crisp) - .5*blur;
vec3 ret1 = crisp + mus + dots;

vec3 mod = texture (sampler_noise_lq,.5*uv+time*.01).xyz-.2;
mod = 1 - mod*diff;

ret = (1-mask)*ret1*mod + mask*ret1*mod;
ret = ret *.98- .03;

}

comp:
shader_body
{
    vec2 uv_echo = (uv - 0.5)*0.993*vec2(-1,1) + 2.5;
    ret = mix( texture(sampler_main, uv).xyz, 
                texture(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma
    ret =  ret; //brighten
ret /= vec3(0.9,0.4,2.2);
}

