comp:
shader_body
{
    vec2 d = texsize.zw;
    vec3 dx = ( texture(sampler_main, uv+vec2(3,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );
    vec3 dy = ( texture(sampler_main, uv+vec2(0,1)*d).xyz-texture(sampler_main, uv-vec2(0,1)-d).xyz );
vec2 dz = vec2(dx.y,dy.y);

d = texsize.zw*2;
dx = ( 2*((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) )*0.5;
dy = ( 2*((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) )*0.5;

vec3 bg = pow(length(dz)*0.1+0,0.7) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2).y-1-0.1;

ret = bg*vec3(0.3,0.5,0.7);
ret = mix(ret,vec3(0.2,0.1,0),texture(sampler_main, uv + vec2(dx.x,dy.x)*texsize.zw*18).xyz.x*6);
ret = mix(ret,1,texture(sampler_main, uv).xyz.z);
ret=1-ret;
//ret = texture(sampler_main, uv).xyz;
}

comp_hlsl:
shader_body
{
    float2 d = texsize.zw;
    float3 dx = ( GetPixel(uv+float2(3,0)*d)-GetPixel(uv-float2(1,0)*d) );
    float3 dy = ( GetPixel(uv+float2(0,1)*d)-GetPixel(uv-float2(0,1)-d) );
float2 dz = float2(dx.y,dy.y);

d = texsize.zw*2;
dx = ( 2*GetBlur1(uv+float2(1,0)*d)-2*GetBlur1(uv-float2(1,0)*d) )*0.5;
dy = ( 2*GetBlur1(uv+float2(0,1)*d)-2*GetBlur1(uv-float2(0,1)*d) )*0.5;

float3 bg = pow(length(dz)*0.1+0,0.7) + GetBlur2(uv).y-1-0.1;

ret = bg*float3(0.3,0.5,0.7);
ret = lerp(ret,float3(0.2,0.1,0),GetPixel(uv + float2(dx.x,dy.x)*texsize.zw*18).x*6);
ret = lerp(ret,1,GetPixel(uv).z);
ret=1-ret;
//ret = GetPixel(uv);
}
