ret.x = tex2D( sampler_fc_main, uv_m ).x*0.53;


float2 d = texsize.zw*4;
float2 uv_z = (uv_orig-0.5)*0.996 + 0.5; // a small dose of zoom-in
float3 dx = ( GetBlur1(uv_z + float2(1,0)*d) - GetBlur1(uv_z-float2(1,0)*d) );
