warp:
shader_body
{

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
vec3 rand = texture(sampler_noise_lq, dither_uv).xyz-0.47;

vec2 d = texsize.zw * 4;
vec3 dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
d = mix(uv_orig,uv,0.36);
vec2 uv_y =d - vec2(dx.y,dy.y)*texsize.zw*4;
vec2 uv_z =d - vec2(dx.z,dy.z)*texsize.zw*4;

ret.y = texture(sampler_main, uv_y).xyz.y - (((texture(sampler_blur1, uv).xyz * scale1) + bias1).y-texture(sampler_main, uv_y).xyz.y)*0.024 - 0.009 + (1-texture(sampler_main, uv_orig).xyz.x)*0.014 + rand*0.02;
ret.z = texture(sampler_main, uv_z).xyz.z - (((texture(sampler_blur1, uv).xyz * scale1) + bias1).z-texture(sampler_main, uv_z).xyz.z)*0.024 - 0.009 + (0+texture(sampler_main, uv_orig).xyz.x)*0.014 + rand*0.02;

ret.x = texture( sampler_fc_main, uv+rand*texsize.zw*.5 ).x;
ret.x += (ret.x - ((texture(sampler_blur3, uv).xyz * scale3) + bias3).x)*0.4 + rand*0.004;

//ret = 0;
}

comp:
shader_body
{
    vec2 d = texsize.zw;
    vec3 dx = ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );
    vec3 dy = ( texture(sampler_main, uv+vec2(0,1)*d).xyz-texture(sampler_main, uv-vec2(0,1)*d).xyz );
vec2 dz = vec2(dx.y,dy.y)*3;

d = texsize.zw*2;
dx = ( 2*((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) )*0.5;
dy = ( 2*((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-2*((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) )*0.5;

dz +=vec2(dx.y,dy.y);

vec3 bg = pow(length(dz)*0.8+0,0.7) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2).y*0.4-0.1;

ret = bg*vec3(0.1,0.7,2);
ret = mix(ret,vec3(1.5,0.5,0.3),texture(sampler_main, uv + vec2(dx.x,dy.x)*texsize.zw*18).xyz.x*6);
ret = mix(ret,1,texture(sampler_main, uv).xyz.z);
ret=1-ret;
//ret = texture(sampler_main, uv).xyz;
