frame_eqs_eel:
dx=0;
warp=0;
rot=sin(time*3.14*20)*0.002;
monitor=bass;
q3=int(rand(1000))/1000;
q2=q3+int(rand(1000))/4000;
all=(bass_att+treb_att+mid_att)/20+all;
wave_r=q3*0.1+sin(all*0.6)*0.9;
wave_g=sin(4*sin(all*1.0));
wave_b=sin(cos(all*1.11)*4);

warp:
shader_body
{
    vec2 scale = vec2(1280,1024)*texsize.zw; // 1280x1024 : the resolution i modelled this shader with
    float d = 0.005;
    float dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).x*scale.x;
    float dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).x*scale.y;
    float dxb = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).x*scale.x;
    float dyb = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).x*scale.y;
    vec2 my_uv = uv - vec2(dx,dy)*0.01 + vec2(dxb,dyb)*0.003;



    vec2 v = 0.01;
    ret.x = texture( sampler_fw_main, my_uv -floor(my_uv)*0).x;


    ret.x += (ret.x - ((texture(sampler_blur3, uv).xyz * scale3) + bias3).x)*.1;
    ret.x -= 0.01;


//--------------------------------

    my_uv = uv + vec2(dy,-dx)*0.05*(1.2-((texture(sampler_blur3, uv).xyz * scale3) + bias3).y);



    v = 0.01;
    ret.z = texture( sampler_fw_main, my_uv).z;

    ret.z += (ret.z - ((texture(sampler_blur1, uv).xyz * scale1) + bias1).z)*length(my_uv-uv)*180/length(scale);
    ret.z *= 0.8;
    ret.z -= 0.01;



//--------------------------------

    d = 0.01;
    my_uv = vec2(-dy,dx)*0.045;

    dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).y*scale.x;
    dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).y*scale.y;
    my_uv += uv - vec2(dx,dy)*0.03;
    v = 0.01;
    ret.y =texture( sampler_fw_main, my_uv).y;
    ret.y += (ret.y - ((texture(sampler_blur3, my_uv).xyz * scale3) + bias3).y)*0.1 - 0.01;





//ret = 0;
}

comp:
shader_body
{
ret = mix(((texture(sampler_blur2, uv).xyz * scale2) + bias2),texture(sampler_main, uv).xyz,0.44);

ret = (ret.y-ret.x*0.5)*mix(vec3(2.4,2.7,2.8),1,ret.x)*2.4;
//MilkDrop3 Burn Mode:
ret = mix(ret,2,texture(sampler_main, uv).xyz.z*1.36);
//MilkDrop3 Solarize Mode:
ret = ret*(1-ret)*4;
}

warp_hlsl:
shader_body
{
    float2 scale = float2(1280,1024)*texsize.zw; // 1280x1024 : the resolution i modelled this shader with
    float1 d = 0.005;
    float1 dx = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).x*scale.x;
    float1 dy = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).x*scale.y;
    float1 dxb = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).x*scale.x;
    float1 dyb = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).x*scale.y;
    float2 my_uv = uv - float2(dx,dy)*0.01 + float2(dxb,dyb)*0.003;



    float2 v = 0.01;
    ret.x = tex2D( sampler_fw_main, my_uv -floor(my_uv)*0).x;


    ret.x += (ret.x - GetBlur3(uv).x)*.1;
    ret.x -= 0.01;


//--------------------------------

    my_uv = uv + float2(dy,-dx)*0.05*(1.2-GetBlur3(uv).y);



    v = 0.01;
    ret.z = tex2D( sampler_fw_main, my_uv).z;

    ret.z += (ret.z - GetBlur1(uv).z)*length(my_uv-uv)*180/length(scale);
    ret.z *= 0.8;
    ret.z -= 0.01;



//--------------------------------

    d = 0.01;
    my_uv = float2(-dy,dx)*0.045;

    dx = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).y*scale.x;
    dy = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).y*scale.y;
    my_uv += uv - float2(dx,dy)*0.03;
    v = 0.01;
    ret.y =tex2D( sampler_fw_main, my_uv).y;
    ret.y += (ret.y - GetBlur3(my_uv).y)*0.1 - 0.01;





//ret = 0;
}

comp_hlsl:
shader_body
{
