warp:
#define MyGet GetPixel //GetBlur1
shader_body
{
    // GROW EFFECT - bright pixels spread radially outward.
    // BE CAREFUL - this can really thrash the texture cache! (SLOW)
    float grad_rad = 3;  //TWEAK
    float str = 2.3;      //TWEAK
    vec3 d = vec3(texsize.zw, 0) * grad_rad;
    vec4 lums = 0;
    lums.x = lum( MyGet(uv + texsize.zw*d.xz) );
    lums.y = lum( MyGet(uv - texsize.zw*d.xz) );
    lums.z = lum( MyGet(uv + texsize.zw*d.zy) );
    lums.w = lum( MyGet(uv - texsize.zw*d.zy) );
    vec2 grad = vec2(lums.x-lums.y, lums.z-lums.w)*str*1000/grad_rad;
    grad = (clamp(grad*0.5+0.5, 0.0, 1.0)*2-1)*1.4;
    // sample previous frame
    ret = texture( sampler_fc_main, uv + grad*texsize.zw ).xyz;

    // darken (decay) over time
    ret *= 0.965; //or try: ret -= 0.004;
}

comp:
shader_body
{
ret = mix(((texture(sampler_blur2, uv).xyz * scale2) + bias2),texture(sampler_main, uv).xyz,0.44);

ret = (ret.y-ret.x*0.5)*mix(vec3(2.4,2.7,2.8),1,ret.x)*2.4;
//MilkDrop3 Burn Mode:
ret = mix(ret,2,texture(sampler_main, uv).xyz.z*1.36);
}

