warp:
shader_body
{

   uv = uv - vec2(0,1)*texsize.zw*0;

    float d = 0.024;
    float dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).y*1280*texsize.z;
    float dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).y*1024*texsize.w;
    vec2 my_uv = uv - vec2(dx,dy)*0.05;



    ret.y = texture( sampler_fc_main, my_uv - floor(my_uv)).y;
    ret.y += (ret.y - ((texture(sampler_blur1, my_uv - floor(my_uv)).xyz * scale1) + bias1).y - 0.131)*0.13 +0.017;
    ret.z = (texture(sampler_main, uv_orig).y - ret.y)*2 + 0.5;

    d = 0.014;
    dx = ( ((texture(sampler_blur2, uv + vec2(d,0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(d,0)).xyz * scale2) + bias2) ).x*1280*texsize.z;
    dy = ( ((texture(sampler_blur2, uv + vec2(0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, uv-vec2(0,d)).xyz * scale2) + bias2) ).x*1024*texsize.w;
    my_uv = uv + vec2(dx,dy)*0.01;

    ret.x = texture( sampler_fw_main, my_uv).x;


    ret.x += (ret.x - ((texture(sampler_blur3, uv).xyz * scale3) + bias3).x - 0.02)*.5;

//ret = 0;

}

comp:
float a,b,c;
shader_body
{
    vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz*0.8 - 0.4 ;
a =base.x/4;b = base.y/4; c = base.z/4;
c = c + b + a*b;
a = a + c + a*c;
base = .3*a;
ret = base*(1.3-base)*(0.7-base)*2*vec3(2.2,0.4,1.2) + pow(base,2)*vec3(2.9,2.5,0.8); //MilkDrop2077 color mod
ret.xy = ret.yx;
ret *= 1.8;
ret -= 1.4;
ret /= vec3(0.1,1.0,-1.4);
}

