warp:
shader_body
{
vec2 uv_m = 1.0 - abs( fract( uv * .5 ) * 2.0 - 1.0 ); // Eo.S' mirror code
ret.x = texture( sampler_fc_main, uv_m ).z; // first julia fractal iteration
ret.y = mix(mix(texture( sampler_fc_main, uv_m ).x, ret.x,.5), 
             texture(sampler_main, uv_orig).xyz.y,
             0.25); // 75% of motion blur to the green channel


vec2 d = texsize.zw*2;
vec2 uv_z = (uv_orig-0.5)*0.998 + 0.5; // a small dose of zoom-in
vec3 dx = ( ((texture(sampler_blur1, uv_z + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_z-vec2(1,0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv_z + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_z-vec2(0,1)*d).xyz * scale1) + bias1) );
uv_z -=vec2(dx.z,dy.z)*texsize.zw*2; //shortening of the nebulous matter
ret.z = max((tex2d(sampler_fc_main,uv_m).x-0.5)*3*clamp(1-length(uv_orig-0.15)*3.2, 0.0, 1.0), // feeded by the inner of the red channel
             tex2d(sampler_fc_main,uv_z).z);
ret.z += (tex2d(sampler_fc_main,uv_z).z - ((texture(sampler_blur1, clamp(uv_z, 0.0, 1.0)).xyz * scale1) + bias1).z)*0.02; // sort of geiss' skin dot code
}

comp:
float a,b,c;
shader_body
{
uv *=2 ;
vec2 m = floor(fract(uv*0.5)*2);
uv = fract(uv)*(1-m) + (m)*fract(1-uv);

uv = 0.5 + (uv-0.5)*vec2(0.75,-0.9) - vec2(0,0.05);
    vec3 base = ((texture(sampler_blur3, uv).xyz * scale3) + bias3) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2) + ((texture(sampler_blur1, uv).xyz * scale1) + bias1) + texture(sampler_main, uv).xyz + 0.04;
a =base.x/4;b = base.y/4; c = base.z/4;
c = c + b - c*b;
a = a + c - a+c;
base = 1.2*a;
ret = base*(1-base)*(1-base)*2*vec3(.5,3.1,4) - pow(base,2)*vec3(-.2,.2,.1);
ret -= 0.32;
//ret.xy = ret.yx;
ret /= vec3(2.8,1.5,0.6);
}

