frame_eqs_eel:
warp = 0;
zoom = 1;
dt = 0.02;

h = h + v*dt;

v = v*0.986 + a*dt;
a = -(h - 0.5)*0.4 + sqrt(- min(0,(w1v+w2v)*0.1)) - .175;

monitor = w2;


w1 = w1 + w1v*dt;
w1v = w1v*0.98 + w1a*dt;
w1a = -(w1)*20 - w2a*0.1   + av*30;

w2 = w2 + w2v*dt;
w2v = w2v*0.98 + w2a*dt;
w2a = -(w2-w1)*20;

bb = bb*0.99 + bass*0.01;
mm = mm*0.99 + mid*0.01;
tt = tt*0.99 + treb*0.01;

mi = min(min(bb,mm),tt);
ma = max(max(bb,mm),tt);
av = (bb+mm+tt-mi-ma);
q5 = av;
avn = (av-mi)/(ma-mi);
av = abs(avo-avn);
avo = avn;

q1 = h*0.8 + 0.12;
q2 = (w1);
q3 = (w2 + w1);
q4 = 0.1+ avo*0.1;

