warp:
shader_body
{
uv = mix(uv_orig,uv,bass);

vec2 d = texsize.zw * 8;
vec3 dx = ( ((texture(sampler_blur1, uv + vec2(10,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*bass*6).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv + vec2(4,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(3,1)*bass*6).xyz * scale1) + bias1) );

uv += vec2(lum(dx),lum(dy))*texsize.zw*35;
ret = tex2d(sampler_fc_main, uv) - 0.009;

}

comp:
shader_body
{
    uv = 0.05 + 0.9*uv;
    ret = texture(sampler_main, uv).xyz;
      // SUPER GLOW EDGES - looks awesome w/octopus
      vec3 avg_col = ((texture(sampler_blur2, uv).xyz * scale2) + bias2);
      ret = abs(avg_col - ret)*6;
    ret *= .65; // a little bit of overbright
}

