frame_eqs_eel:
warp = 0;

tic = min(time-tin,.1);
tin = time;

vol = (bass_att + treb_att + mid_att)*.333333;

ra = 1/tic*.25;

treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);
vav = tic*(vav*(1/tic - ra) + ra*(bass+treb+mid)*.33333);

tt = tt + tic*treb;
mt = mt + tic*mid;
bt = bt + tic*bass;
vt = vt + tic*vav;

q1 = tt*4;
q2 = mt*4;
q3 = bt*4;

st = .015;
q4 = treb_avg*treb_avg*st;
q5 = mid_avg*mid_avg*st;
q6 = bass_avg*bass_avg*st;
q4 = treb_avg*st;
q5 = mid_avg*st;
q6 = bass_avg*st;


