init_eqs_eel:
beatrate=5;
avgbeatrate=1;
bbtime=1;
lbbtime=.5;
avgdb=.5;
vol=1;
avgvol=.2;
mtime=1000;

tt=1000*treb;
bt=1000*bass;
mt=1000*mid;

frame_eqs_eel:
chng=sin(time*.5);
cthr=.9999;
mq21=if(above(chng,cthr),rand(3),mq21);
mq22=if(above(chng,cthr),rand(3),mq22);
mq23=if(above(chng,cthr),rand(3),mq23);
mq24=if(above(chng,cthr),rand(2),mq24);
mq25=if(above(chng,cthr),rand(2),mq25);
mq26=if(above(chng,cthr),rand(2),mq26);
mq27=if(above(chng,cthr),rand(1),mq27);
mq28=if(above(chng,cthr),rand(1),mq28);
mq29=if(above(chng,cthr),rand(1)*.3,mq29);
mq31=if(above(chng,cthr),rand(1)*.3,mq31);
monitor=chng;
q21=mq21;q22=mq22;q23=mq23;q24=mq24;q25=mq25;q26=mq26;
q27=mq27;q28=mq28;q29=mq29;q31=mq31;

monitor=mq1;
vol=bass+treb+mid;
atime=atime+vol;
q11=.4+sin(atime*.006        )*.4;
q12=.4+cos(atime*.00613828348)*.4;
q13=.4+sin(atime*.00598593455)*.4;
monitor=q13;

db=(bass-lbass)*fps;
lbass=bass;
avgdb=avgdb*.99+abs(db)*.01;

//beatdection
rawbeatb=above(db,avgdb*2);
beatb=rawbeatb*above(time-lbbtime,.75*avgbeatrate)*above(bass_att,.3);

//beatrate calc
bbtime=time*beatb;
beatrate=beatb*(bbtime-lbbtime)+(1-beatb)*beatrate;
avgbeatrate=beatb*(avgbeatrate*.999+beatrate*.001)+(1-beatb)*avgbeatrate;
lbbtime=time*beatb+(1-beatb)*lbbtime;

bc=bc+beatb;



vol=(rawbeatb*(db-(avgdb))*.01);
avgvol=avgvol*.99+vol*.05;

mtime=mtime+min(avgvol*.5,.25);

//decay=.99;
warp=0;
wrap=1;


warp = 0;

tic = min(time - tin,.1);
tin = time;

ra = 10;
treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);

rb = 1;
vav = tic*(vav*(1/tic - rb) + rb*(bass+treb+mid)*.33333);

tt = tt + tic*treb_avg;
mt = mt + tic*mid_avg;
bt = bt + tic*bass_avg;
vt = vt + tic*(treb_avg+mid_avg+bass_avg)*.33333;

sz = .2;

q1 = .5 + sz*sin(tt*.629) - sz*sin(tt*.107);
q2 = .5 + sz*sin(tt*.987) - sz*sin(tt*.456);

q3 = .5 + sz*sin(mt*.654) - sz*sin(mt*.355);
q4 = .5 + sz*sin(mt*.548) - sz*sin(mt*.875);

q5 = .5 + sz*sin(bt*.687) - sz*sin(bt*.318);
q6 = .5 + sz*sin(bt*.465) - sz*sin(bt*.526);


q7=mtime;
q8=dbass;

pixel_eqs_eel:
xd = q1-x;
yd = q2-y;

dis = pow(xd*xd+yd*yd,.5);
des = 1 - dis*.7071068;
des = pow(des,10*q8)*-treb;
an = acos(abs(xd)/dis);

dx = sign(xd)*.01*cos(an)*des;
dy = sign(yd)*.01*sin(an)*des;

//dy = dy + sign(xd)*treb*.05*cos(an)*des;
//dx = dx + sign(yd)*treb*.05*sin(an)*des*-1;


dx=dx-sign(yd)*sin(q7+an)*.001*treb_att*des;
dy=dy+sign(xd)*cos(q7+an)*.001*treb*des;

sy=1+dx*des;
sx=1-dy*des;


xd = q3-x;
yd = q4-y;

dis = pow(xd*xd+yd*yd,.5);
des = 1 - dis*.7071068;
des = pow(des,10*q8)*-mid;
an = acos(abs(xd)/dis);

dx = dx + sign(xd)*.01*cos(an)*des;
dy = dy + sign(yd)*.01*sin(an)*des;

//dy = dy + sign(xd)*mid*.05*cos(an)*des;
//dx = dx + sign(yd)*mid*.05*sin(an)*des*-1;


dx=dx-sign(yd)*sin(q7+an)*.001*mid_att*des;
dy=dy+sign(xd)*cos(q7+an)*.001*mid*des;

sy=1+dx*des;
sx=1-dy*des;

xd = q5-x;
yd = q6-y;

dis = pow(xd*xd+yd*yd,.5);
des = 1 - dis*.7071068;
des = pow(des,10*q8)*-bass;
an = acos(abs(xd)/dis);

dx = dx + sign(xd)*.01*cos(an)*des;
dy = dy + sign(yd)*.01*sin(an)*des;

//dy = dy + sign(xd)*bass*.05*cos(an)*des;
//dx = dx + sign(yd)*bass*.05*sin(an)*des*-1;


dx=dx-sign(yd)*sin(q7+an)*.001*bass_att*des;
dy=dy+sign(xd)*cos(q7+an)*.001*bass*des;

sy=1+dx*des;
sx=1-dy*des;

warp:
shader_body
{

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
vec3 rand = texture(sampler_noise_lq, dither_uv).xyz-0.5;

vec2 d = texsize.zw * 4;
vec3 dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
d = mix(uv_orig,uv,0.35);
vec2 uv_y =d - vec2(dx.y,dy.y)*texsize.zw*4;
vec2 uv_z =d - vec2(dx.z,dy.z)*texsize.zw*4;

ret.y = texture(sampler_main, uv_y).xyz.y - (((texture(sampler_blur1, uv).xyz * scale1) + bias1).y-texture(sampler_main, uv_y).xyz.y)*0.024 - 0.009 + (1-texture(sampler_main, uv_orig).xyz.x)*0.014 + rand*0.02;
ret.z = texture(sampler_main, uv_z).xyz.z - (((texture(sampler_blur1, uv).xyz * scale1) + bias1).z-texture(sampler_main, uv_z).xyz.z)*0.024 - 0.009 + (0+texture(sampler_main, uv_orig).xyz.x)*0.014 + rand*0.02;

ret.x = texture( sampler_fc_main, uv+rand*texsize.zw*.5 ).x;
ret.x += (ret.x - ((texture(sampler_blur3, uv).xyz * scale3) + bias3).x)*0.4 + rand*0.0052;

//ret =vec3(0);
}

comp:
shader_body
{

ret=((texture(sampler_blur1, uv).xyz * scale1) + bias1);
ret+=((texture(sampler_blur1, vec2(uv.x+ret.x,uv.y+ret.y)).xyz * scale1) + bias1);
ret+=((texture(sampler_blur1, vec2(uv.x-ret.y,uv.y-ret.y)).xyz * scale1) + bias1);
vec3 noise9 = texture(sampler_noisevol_hq, 
                      ((uv.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*q31
                     ).xyz;
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret=pow(length(ret)*ret*1.33,0.89-((texture(sampler_blur3, uv).xyz * scale3) + bias3));
//ret=clamp(ret, 0.0, 1.0);
//ret*=texture(sampler_noise_lq,uv*ret.y).xyz;
ret*=ret.z;
ret=ret-texture(sampler_main, uv).xyz;
}

warp_hlsl:
shader_body
{

float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
float3 rand = tex2D(sampler_noise_lq, dither_uv).xyz-0.5;

float2 d = texsize.zw * 4;
float3 dx = ( GetBlur1(uv + float2(1,0)*d) - GetBlur1(uv-float2(1,0)*d) );
float3 dy = ( GetBlur1(uv + float2(0,1)*d) - GetBlur1(uv-float2(0,1)*d) );
d = lerp(uv_orig,uv,0.35);
float2 uv_y =d - float2(dx.y,dy.y)*texsize.zw*4;
float2 uv_z =d - float2(dx.z,dy.z)*texsize.zw*4;

ret.y = GetPixel(uv_y).y - (GetBlur1(uv).y-GetPixel(uv_y).y)*0.024 - 0.009 + (1-GetPixel(uv_orig).x)*0.014 + rand*0.02;
ret.z = GetPixel(uv_z).z - (GetBlur1(uv).z-GetPixel(uv_z).z)*0.024 - 0.009 + (0+GetPixel(uv_orig).x)*0.014 + rand*0.02;

ret.x = tex2D( sampler_fc_main, uv+rand*texsize.zw*.5 ).x;
ret.x += (ret.x - GetBlur3(uv).x)*0.4 + rand*0.0052;

//ret = 0;
}

comp_hlsl:
shader_body
{

ret=GetBlur1(uv);
ret+=GetBlur1(float2(uv.x+ret.x,uv.y+ret.y));
ret+=GetBlur1(float2(uv.x-ret.y,uv.y-ret.y));
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q31
                     );
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret=pow(length(ret)*ret*1.33,0.89-GetBlur3(uv));
//ret=saturate(ret);
//ret*=tex2D(sampler_noise_lq,uv*ret.y);
ret*=ret.z;
ret=ret-GetPixel(uv);
}
