warp:
shader_body
{

    vec2 d = texsize.zw*4;
    vec3 dx = ((texture(sampler_blur1, fract(uv+vec2(1,0)*d)).xyz * scale1) + bias1)-((texture(sampler_blur1, fract(uv-vec2(1,0)*d)).xyz * scale1) + bias1);
    vec3 dy = ((texture(sampler_blur1, fract(uv+vec2(0,1)*d)).xyz * scale1) + bias1)-((texture(sampler_blur1, fract(uv-vec2(0,1)*d)).xyz * scale1) + bias1);
    vec3 b1 = ((texture(sampler_blur1, uv).xyz * scale1) + bias1) - 0;
    d *= -1;
    float d2 = d*4;
    float c1 = 0.042;
    float c2 = -0.0075;


    vec2 my_uv = uv - vec2(dx.y,dy.y)*d - vec2(dx.x,dy.x)*d2;
    ret.y = texture( sampler_fc_main, my_uv-floor(my_uv)).y;
    ret.y += (ret.y - b1.y)*c1 + c2;


    my_uv = uv - vec2(dx.x,dy.x)*d - vec2(dx.z,dy.z)*d2;
    ret.x = texture( sampler_fc_main, my_uv-floor(my_uv)).x;
    ret.x += (ret.x - b1.x)*c1 + c2;


    my_uv = uv - vec2(dx.z,dy.z)*d - vec2(dx.y,dy.y)*d2;
    ret.z = texture( sampler_fc_main, my_uv-floor(my_uv)).z;
    ret.z += (ret.z - b1.z)*c1 + c2;
vec3 noise9 = texture(sampler_noisevol_hq, 
                      ((my_uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*q29
                     ).xyz;
if(ret.x> q21*q13         && ret.x<= q24*q11        )ret.y -= (noise9).x*.5; 
if(ret.y> q22*q11         && ret.y<= q25*q12        )ret.z -= (noise9).y*.5; 
if(ret.z> q23*q12         && ret.z<= q26*q13        )ret.x -= (noise9).z*.5; 

}

comp:
shader_body
{
    ret =texture(sampler_main, uv).xyz;
    float L = lum(ret);
    float bw = clamp(cos(L*17 + 3 + uv.x*30)*30 - 21, 0.0, 1.0);
    float L2 = lum(((texture(sampler_blur1, uv).xyz * scale1) + bias1));
    float bw2 = clamp(cos(L*17 + 3 + uv.x*30)*3 - 2.7, 0.0, 1.0);

    ret =vec3(vec3(1.1,0.8,0.7)*ret.x +
          vec3(1.4,2.6,3.55)*ret.y + 
          vec3(2.3,1.1,1.3)*ret.z);
    ret *= 0.4;

    ret =ret*ret * (1 + bw2*4);

}

