warp:
shader_body
{

    // sample previous frame
    vec3 a = texture(sampler_main, uv).xyz;
    vec3 b = ((texture(sampler_blur1, uv).xyz * scale1) + bias1)*10;

    vec2 uv2 = uv + (a-b).xy*texsize.zw*3;
    ret = texture(sampler_main, uv2).xyz;

    // add noise
    float t = 0.001;
    ret += (texture(sampler_noise_lq, uv_orig.xy*texsize.xy*texsize_noise_lq.zw).xyz*3-1)*t;

    // darken
    ret -= 0.001;
}

