shader_body
{

    // sample previous frame
    float3 a = tex2D(sampler_main, uv).xyz;
    float3 b = GetBlur1(uv)*10;

    float2 uv2 = uv + (a-b).xy*texsize.zw*3;
    ret = tex2D(sampler_main, uv2).xyz;

    // add noise
    float t = 0.002;
    ret += (tex2D(sampler_noise_lq, uv_orig.xy*texsize.xy*texsize_noise_lq.zw).xyz*3-1)*t;

    // darken
    ret -= 0.001;
}

