frame_eqs_eel:
red_value = if(above(bass,1.15),3*(1-bass)+abs(sin(time*0.3)*0.1),if(above(treb,1.0),0, sin(time/2.5)/10+0.65));
blue_value = if(above(bass,1.15),0.5+abs(sin(time*0.3)*0.1),if(above(treb,1.0),0.7+0.1*sin(time*1.123), 0.3-treb-sin(time/2.5)/10));
green_value = if(above(bass,1.15),1-abs(sin(time*0.3)*0.1),if(above(treb,1.0),0.6+0.1*sin(time*1.123), sin(time/2.5)/10+0.3));
wave_r = red_value;
wave_b= blue_value;
wave_g = green_value;
q1 = (cx*2-1) + 0.62*( 0.60*sin(0.374*time) + 0.40*sin(0.294*time) );
q2 = (cy*2-1) + 0.62*( 0.60*sin(0.393*time) + 0.40*sin(0.223*time) );
q3 = (cx*2-1) + 0.62*( 0.60*sin(0.174*-time) + 0.40*sin(0.364*time) );
q4 = (cy*2-1) + 0.62*( 0.60*sin(0.234*time) + 0.40*sin(0.271*-time) );
decay = decay - 0.01*equal(frame%5,0);

pixel_eqs_eel:
du = (x+rad)*2-1 - q1;
dv = (y+rad)*2-1 - q2;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv);
mult = 0.008/(dist+0.4);
dx = mult*sin(ang2-1.5)/4;
dy = mult*cos(ang2-1.5)/4;
du = (1-rad)*2-1 - q3;
dv = (1-rad)*2-1 - q4;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv);
mult = 0.008/(dist+0.4);
dx = dx + mult*sin(ang2+.5)/3;
dy = dy + mult*cos(ang2+.5)/3;

warp:
shader_body
{

    // sample previous frame
    vec3 a = texture(sampler_main, uv).xyz;
    vec3 b = ((texture(sampler_blur1, uv).xyz * scale1) + bias1)*10;

    vec2 uv2 = uv + (a-b).xy*texsize.zw*3;
    ret =texture(sampler_main, uv2).xyz;

    // add noise
    float t = 0.002;
    ret += (texture(sampler_noise_lq, uv_orig.xy*texsize.xy*texsize_noise_lq.zw).xyz*3-1)*t;

    // darken
    ret -= 0.001;
}

comp:
float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body
{

vec2 uvm = uv;
ret =vec3(texture(sampler_main, uvm).x),

ret =mix (
((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x + ((texture(sampler_blur3, uvm).xyz * scale3) + bias3).x,
ret,
_pow(ret,.5));

ret *= 2.7;

vec2 uv3 = (uv-.5)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).zzz)+.5;

ret += clamp(_pow(texture(sampler_main, uv3).zzz,.5)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x -ret.x), 0.0, 1.0),
ret += clamp(_pow(((texture(sampler_blur1, uv3).xyz * scale1) + bias1).zzz,.5)*(1-((texture(sampler_blur1, uvm).xyz * scale1) + bias1).x -ret.x), 0.0, 1.0),

ret =pow(ret,vec3(.9,.7,.6))*1.25 -.25;
ret -= texture(sampler_main, uvm).yyy;
ret *= ret;

ret-= .04;
vec3 noise9 = texture(sampler_noisevol_hq, 
                      ((uv3.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(1,0,1)*q30 
                     ).xyz;
if(ret.x> q26*q3         && ret.x<= q23*q1        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q1         && ret.y<= q22*q2        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q2         && ret.z<= q21*q3        )ret.y -= (noise9).z*.5; 
ret -= 0.15;
}

