frame_eqs_eel:
bb = bb*0.99 + bass*0.02;
mm = mm*0.99 + mid*0.02;
tt = tt*0.99 + treb*0.02;

mx = max(max(bb,mm),tt);
mn = min(min(bb,mm),tt);

t1 = (bb-mn)/(mx-mn);
t2 = (mm-mn)/(mx-mn);
t3 = (tt-mn)/(mx-mn);

v = 0.2/fps;
bm = bm + (t1-t2)*v;
mt = mt + (t2-t3)*v;

// coordinates spiral 1
q11 = 0.5; // X
q12 = 0; // Y

// coordinates spiral 2
q13 = -0.5;
q14 = 0;

q15 = 0.5/asin(1); // pi/4

q16 = -bm; // rotation
q17 = mt; // growth

// raytraced sphere code

d = 5; // distance of the sphere, the farther you're away the more you'll see on the horizon
z = 0.4; // distance of the pane where the scene is projected on, wider distance = smaller apex angle of the camera, very small distances = fish eyes view

zz = 2.2; // linear zoom

pi = asin(1)*2;
pi2inv = 0.5/pi;
maskangle = asin( 1/d);
q32 = maskangle; // hit = (cameras angle < mask_angle)
q31 = d;
q30 = z;
q29 = pi2inv;
q28 = pi;
q27 = 1/zz;

v = 0.4;
j1 = j1*0.95 + sqr(bass*4)*v;
j2 = j2*0.95 + sqr(mid*4)*v;
j3 = j3*0.95 + sqr(treb*4)*v;

n = n + j1*0.0052;
n1 = n1 + j2*0.0052;
n2 = n2 + j3*0.0052;


w1 = bm*pi;    // solid angles to rotate the sphere in its origin
w2 = mt*pi;
w3 = n2*0.0;

q20 = cos(w1);
q21 = sin(w1);
q22 = cos(w2);
q23 = sin(w2);
q24 = cos(w3);
q25 = sin(w3);

warp:
vec2 numerator, denominator, fraction;

vec2 polar, factorA, factorB, product;
uniform sampler2D sampler_pano_earth;
uniform sampler2D sampler_pano_earth_clouds;
uniform sampler2D sampler_pano_earth_night;

shader_body
{

float z = q30;
float d = q31;
float pi = q28;
float pi2inv = q29;
float mask_apex = q32;
float zoom = q27;

vec2 c = zoom*(uv_orig-0.5)*aspect.xy;

float camera_apex = atan( length(c),z);

bool mask = mask_apex >= camera_apex;

float longitude = atan(c.x,c.y);
float latitude = (pi - camera_apex + asin(d*sin(camera_apex)));

vec2 polar = vec2(longitude,latitude);

vec3 p = vec3( sin(polar.x)*sin(polar.y),cos(polar.x)*sin(polar.y),cos(polar.y));

factorA = p.zx;
factorB = vec2(q22,q23);
product = vec2( factorA.x*factorB.x - factorA.y*factorB.y,factorA.x*factorB.y + factorA.y*factorB.x);
p.zx = product.xy;

factorA = p.zy;
factorB = vec2(q20,q21);
product = vec2( factorA.x*factorB.x - factorA.y*factorB.y,factorA.x*factorB.y + factorA.y*factorB.x);
p.zy = product.xy;

polar = vec2(atan(p.z,p.x),atan(p.y,length(p.xz)))*pi2inv*vec2(1,2)+vec2(0,0.5);


ret = mask*tex2d(sampler_pano_earth,polar);
ret = mix(ret,1,mask*tex2d(sampler_pano_earth_clouds,polar));




float cutoff = 1;
int n = -2;
float s = 0.666;

   c = (uv_orig-0.5)*2*aspect.wz;
   numerator = (c + vec2(q11,q12))*64;
   denominator = c + vec2(q13,q14);
   fraction = vec2( numerator.x*denominator.x + numerator.y*denominator.y,
                      numerator.y*denominator.x - numerator.x*denominator.y)/
                      (denominator.x*denominator.x + denominator.y*denominator.y);
   c = fraction-0.5;
   float ang_hq = n*atan(c.x,c.y);
   vec2 spiral = vec2(ang_hq*q15+q16,s*aspect.w*log(length(c))+q17);
   spiral = 0.5 + (0.5 - abs( fract( spiral * 0.5 ) * 2.0 - 1.0 ))*cutoff; // mirror




ret = mix(ret,texture(sampler_main, spiral).xyz-0.02,1-mask);
vec4 noise2 = texture(sampler_noisevol_hq, 
                      ((uv_orig.xyy
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*0.4 
                     ).xyz;
ret -= (noise2)*.3; 
}

