float size = .015;
float dist = length(ball)  / size;
z = clamp(1-dist, 0.0, 1.0);

//ADD
float mask = (z >= .001); 
rs = q28*rs + (1-q28)*rs.yx;
uv += (1-mask)*.03*(rs.yx) + .6*mask*z*normalize(ball);


