comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;
    float L = lum(ret);
    float bw = clamp(cos(L*27 + 3)*30 - 26, 0.0, 1.0);
    float L2 = lum(((texture(sampler_blur1, uv).xyz * scale1) + bias1));
    float bw2 = clamp(cos(L*27 + 3)*3 - 6, 0.0, 1.0);

    ret = vec3(.1,.6,1.1)*ret.x +
          vec3(.1,.6,.55)*ret.y + 
          vec3(1.5,.6,.2)*ret.z;

    ret = ret*ret * (.5 + bw*bw2*1);

}

