warp:
shader_body
{
uv = mix( uv_orig,uv,0.2);

vec2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw*1.5 + rand_frame.xy;
uv  -= (vec2(0,1)*(lum(texture(sampler_main, uv).xyz-0.35))*texsize.zw*32)*(lum(texture(sampler_main, uv).xyz)-0.4);
ret  = texture(sampler_main, clamp(uv, 0.0, 1.0)).xyz - 0.0011 + (texture(sampler_noise_lq, dither_uv).xyz-0.5)*0.0038;    
}

comp:
shader_body
{
    vec2 d = texsize.zw;
    vec3 dx = ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );
    vec3 dy = ( texture(sampler_main, uv+vec2(0,1)*d).xyz-texture(sampler_main, uv-vec2(0,1)*d).xyz );
vec2 dz = vec2(dx.y,dy.y)*3;

d = texsize.zw*2;
dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

dz +=vec2(dx.y,dy.y);

vec3 bg = pow(length(dz)*0.8+0,0.7) + ((texture(sampler_blur2, uv).xyz * scale2) + bias2).y*0.4-0.1;

ret += bg*vec3(2.3,1.5,1.7);
ret = mix(ret,vec3(.2,.2,0.7),texture(sampler_main, uv + vec2(dx.x,dy.x)*texsize.zw*18).xyz.x*6);
ret = mix(ret,1,texture(sampler_main, uv).xyz.z);

//ret = texture(sampler_main, uv).xyz;
}

