comp:
vec2 d, uv_y;
vec3 dx, dy;
shader_body
{

    d = texsize.zw*18;
    dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

vec3 base = texture(sampler_main, uv).xyz;
vec3 emboss = (dx - dy + 1);

    d = texsize.zw*2;
    dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

float grad = length(vec2(dx.x,dy.x));

ret = mix( vec3(0.6,0.5,0.4)*base.x*emboss.x,
            vec3(8,3,1)*grad,
            grad*8
          ); 

ret /= vec3(2.8,-0.2,1.2);
}

