frame_eqs_eel:
dec_med = pow (0.7, 30/fps);
dec_slow = pow (0.96, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %8;
index2 = (index2 + is_beat*bnot(index))%4;
index3 = (index3 + is_beat*bnot(index)*bnot(index2))%3;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att ;
q27 = index + 1;
q28 = index2;


k1 =  is_beat*equal(index%2,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/2;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;


vol = vol*dec_slow + q26 * (1-dec_slow) + q24*q1;
q10 = q27*q28;;


movex = movex + .03*30/fps;
q29 = movex;
movez = movez + .07*30/fps;
q30 = movez;

q31 = 6 + 4*(1+sin(time/7));

zoom = 1;
dy = .00;
dx = .0;
warp = 0;
