    float1 d = 0.024;
    float1 dx = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).y*1280*texsize.z;
    float1 dy = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).y*1024*texsize.w;
    float2 my_uv = uv - float2(dx,dy)*0.05;



    ret.y = tex2D( sampler_fc_main, my_uv - floor(my_uv)).y;
    ret.y += (ret.y - GetBlur1(my_uv - floor(my_uv)).y - 0.131)*0.13 +0.017;
    ret.z = (tex2D(sampler_main, uv_orig).y - ret.y)*2 + 0.5;

    d = 0.014;
    dx = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).x*1280*texsize.z;
