shader_body
{
   float d = texsize.zw;
   vec3 deltax = (texture(sampler_main, uv + vec2(1,0)*d).xyz + texture(sampler_main, uv + vec2(1,0)*d).xyz)*0.5;    
   vec3 deltay = (texture(sampler_main, uv + vec2(0,1)*d).xyz + texture(sampler_main, uv + vec2(0,1)*d).xyz)*0.5;    


   ret = ((deltax+deltay - texture(sampler_main, (uv-0.5)*0.9 + 0.5 ).xyz)); 
   ret -= 0.4;
}

comp:
shader_body
{

    vec2 uv_echo = (uv - 0.5)*1.000*vec2(-1,-1) + 0.5;
    ret = mix( texture(sampler_main, uv).xyz, 
                texture(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma
    vec3 retish = 1 - ret*(1-ret)*4; //solarize
    ret = ((texture(sampler_blur3, uv).xyz * scale3) + bias3)*2 + ((texture(sampler_blur3, uv_echo).xyz * scale3) + bias3)*2 + ret;
}

