warp:
shader_body
{
  // sample previous frame
  ret = texture( sampler_pw_main, uv ).xyz;

  float n;
  vec2 d = texsize.zw*1;
  n  = clamp(texture(sampler_pw_main, uv+d*vec2( 1, 1)).x*999, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 1, 0)).x*999, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 1,-1)).x*999, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 0, 1)).x*999, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2( 0,-1)).x*999, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2(-1, 1)).x*999, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2(-1, 0)).x*999, 0.0, 1.0);
  n += clamp(texture(sampler_pw_main, uv+d*vec2(-1,-1)).x*999, 0.0, 1.0);

  if (n < 1.5)
    ret = 0;
  if (n > 3.5)
    ret = 0;
  if (ret.x < 0.1 && abs(n-3) < 0.5)
    ret = 1;

  // darken (decay) over time
 // ret = (ret - 0.016)*0.985;
}

comp:
shader_body
{
    // use 3D noise 
    vec3 uvw = vec3(uv*8, 0.3);
    //uvw = (rot_s2 * uvw);
    uvw.z += time*0.3;

    // sample a high-quality 3D noise volume!
    vec4 N = texture(sampler_noisevol_hq, uvw).xyz;

    vec2 uv2 = uv;
    uv2 += (N.xy*2-1)*texsize.zw*3;

    ret = texture(sampler_main, uv2).xyz;
    ret += ((texture(sampler_blur1, uv).xyz * scale1) + bias1)*0.4;

    // palettize:
    ret = pow(ret, vec3(0.5, 0.8, 1.7));

    //ret.xyz *= 2; // a little bit of overbright
//MilkDrop3 Color Mode:
ret /= vec3(-1.2,-1.1,-0.3);
//MilkDrop3 Blow Mode:
vec3 avg_col = ((texture(sampler_blur2, uv).xyz * scale2) + bias2);ret = abs(avg_col - ret)*2;
}

