comp:
shader_body
{
    vec2 ret2 = texture(sampler_main, uv).xy;
    vec2 p = (uv* 2.0 +ret2- vec2(1, 1));
    vec2 d = vec2(-1, 1);
    vec2 c = (mat2(1, 1, d / (0.5 + 5.0 / dot(5.0 * p - d, 5.0 * p - d))) * p);
    vec2 v = c;
    v = (mat2(cos(log(length(v)) + time * 0.5 + vec4(0, 33, 11, 0))) * v) * 5.0;

    vec4 o = 0;
    for(float i = 0; i < 9; i++) {
        o += sin(vec4(v.x, v.y, v.y, v.x)) + 1.0;
        v += 0.9 * sin(vec2(v.y, v.x) * i + time) / i + 0.5;
    }

    ret = 1.0 - exp(-exp(c.x * vec4(.3, -1.1, -1, 0)) 
    / o / (0.1 + 0.1 * pow(length(sin(v / 0.3) * 0.2 + c * vec2(1, 2)) - 1.0, 1.0)) 
    / (1.0 + 7.0 * exp(0.3 * c.y - dot(c, c))) / (0.03 + abs(length(p) - 0.7)) * 0.2);
