warp:
shader_body
{
    //Shader By XorDev (x.com/XorDev), ported to MilkDrop by MilkDrop2077

    // Get screen coordinates
    vec2 fragCoord = uv * texsize;
    vec2 r = texsize;
    vec3 FC = vec3(fragCoord, 1.0);

    float t = time;

    // Initialize output
    vec4 o = vec4(0.0, 0.0, 0.0, 0.0);

    // Main shader logic
    vec3 p = vec3(0.0, 0.0, 0.0);
    float i = 0.0;
    float z = 0.0;
    float f = 0.0;

    // Outer loop - 50 iterations
    for(int iter = 0; iter < 50; iter++) {
        i += 1.0;

        // Update z and calculate distance field
        f = 0.003 + 0.1 * abs(length(p) - 5.0);
        z += f;

        // Add to output color (RGB channels only)
        o.rgb += (p / z + 0.8) / f;

        // Update ray position
        p = z * (FC.rgb * 2.0 - vec3(r.x, r.y, r.y)) / r.y;
        p.z += 9.0;

        // Inner loop - 7 iterations for fractal detail
        for(int j = 1; j <= 7; j++) {
            f = float(j);
            p += sin(round(p.zxy / 0.1) * 0.1 * f - t) / f;
        }
    }

    // Apply final transformation
    o = tanh(o / 2000.0);

    // Output final color
    ret = o.rgb;
}

