wave_r = wave_r + 0.15*sin(time*0.517);
wave_g = wave_g + 0.15*sin(time*0.613);
wave_b = wave_b + 0.15*sin(time*0.741);
// Store bass_att in a custom variable for shader access
q1 = bass_att;
// Smoothed bass for more fluid movement
