warp:
shader_body
{
    // Shader By XorDev (x.com/XorDev), ported to MilkDrop by MilkDrop2077

    vec2 r = texsize.xy;
    vec2 p = ((uv.xy - 0.5)*r); // FC.xy - r*.5
    vec4 o = vec4(0,0,0,0);
    float t = time;

    for (float i = 0.0; i < 19.0; i += 1.0)
    {
        float a = i * i / 180.0 - length(p) / r.y;  // i*i/8e1 - length(p)/r.y
        a = max(a, -a * 3.0);                      // max(..., -a*3.)
        float att = 0.01 / (a + 2.0 / r.y);        // .01/(... + 2./r.y)

        float angle = atan(p.y, p.x);             // atan(p.y,p.x)
        a = cos(i - t+q1);                            // cos(i-t)
        float mask = smoothstep(a, 2.0, cos(angle + a + i * i)); // smoothstep(...)

        float hue = angle + i;
        vec3 c = 1.2 + sin(vec3(hue, hue + 2.0, hue + 4.0)); // (1.2+sin(...))

        o.rgb += att * mask * c;
    }

    o = tanh(o);
    ret = vec4(o.rgb, 1.0);
}

