warp:
shader_body{

//Shader from https://x.com/YoheiNishitsuji/status/1923362809569837131
//Ported to MilkDrop by MilkDrop2077

    float aspect = aspect.x / aspect.y;
    vec2 u = (uv - 0.5) * vec2(aspect, 1.0) * 4.0;
    vec4 o = vec4(0.0, 0.0, 0.0, 0.0);
    float t = time/3;
    float g = 0.0;

    for(float i = 0.0; i < 99.0; i += 1.0) {
        vec3 p = vec3(u.x, u.y, g - 6.0);
        vec3 A = vec3(0.0, 9.0, -3.0)/9.5;
        p.y += 1.0;

        // 3D rotation
        p = A * dot(p+p, A) - p - 0.34*cross(p, A);

        // 2D rotation
        mat2 rot = mat2(
            cos(t*0.3), -sin(t*0.3),
            sin(t*0.3), cos(t*0.3)
        );
        p.xz = (rot * p.xz);

        float s = 6.0;
        float e;

        // Fractal iteration
        for(int j = 0; j < 13; j++) {
            e = 16.0 / dot(p, p);
            s *= e;
            p = vec3(0.0, 4.03, -0.5) - abs(abs(p)*e - vec3(3.0, 4.0, 3.0));
        }

        g += 0.3 * p.y*p.y / s;
        o += (log2(s) - g*0.8) / 700.0 * vec4(0.9, 1.0, 1.0, 0.0);
    }

    ret = vec4(tanh(o.x), tanh(o.y), tanh(o.z), 1.0);
}

