warp:
shader_body{

//Shader from https://x.com/YoheiNishitsuji/status/1923362809569837131
//Ported to MilkDrop by MilkDrop2077

    float aspect = aspect.x / aspect.y;
    vec2 u = (uv - 0.5) * vec2(aspect, 1.0) * 2.0;
    vec4 o = vec4(0.0, 0.0, 0.0, 0.0);
    float t = time;
    float g = 0.0;

    for(float i = 0.0; i < 99.0; i += 1.0) {
        vec3 p = vec3(4.5 * u.x, 4.5 * u.y, g - 7.0);
        vec3 A = vec3(0.0, 9.0, -3.0)/9.5;
        p.y += 1.0;

        // 3D rotation
        p = A * dot(p+p, A) - p - 0.14*cross(p, A);

        // 2D rotation on ZY plane
        float angle = t * 0.92;
        mat2 rot = mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
        p.zy = (rot * p.zy);

        float s = 6.0;
        float e;

        // Modified fractal iteration with time-based variation
        for(float j = 0.0; j < 7.0; j += 1.0) {
            e = 18.0 / dot(p, p);
            s *= e;
            float xSize = 3.0 + 2.5 * sin(t * 0.5);
            p = vec3(0.0, 4.213, 0.0) - abs(abs(p)*e - vec3(xSize, 4.0, 3.0));
        }

        g += 0.2 * p.y * p.y / s;
        o += (log2(s) - g * 0.7) / 700.0 * vec4(0.9, 1.0, 1.0, 0.0);
    }

    ret =vec4(tanh(o.x * 2.0), tanh(o.y * 2.0), tanh(o.z * 2.0), 1.0).xyz;
}

