shapes.frame_eqs_eel:
sample=instance/num_inst;         c=instance%2;

//// yin-yang

t=(5+4.5*sin(.2*time))*sample-.4*time;
x1=.3*sample*sin(t);                  y1=.2*cos(t);
x=.5+ if(c,-x1,x1)/q6;                y=.5+ if(c,y1,-y1)/q5;

rad=rad*(1-pow(.1,1-sample));         ang=if(c,-t/2,t/2);

r=if(c,sample,1-sample);     r2=r;    border_r=1-r;        
g=.7*r;                      g2=g;    border_g=.7*(1-r);
b=.3*r;                      b2=b;    border_b=.3*(1-r);

frame_eqs_eel:
chng=sin(time*.5);
cthr=.9999;
mq21=if(above(chng,cthr),rand(3),mq21);
mq22=if(above(chng,cthr),rand(3),mq22);
mq23=if(above(chng,cthr),rand(3),mq23);
mq24=if(above(chng,cthr),rand(2),mq24);
mq25=if(above(chng,cthr),rand(2),mq25);
mq26=if(above(chng,cthr),rand(2),mq26);
mq27=if(above(chng,cthr),rand(1),mq27);
mq28=if(above(chng,cthr),rand(1),mq28);
mq29=if(above(chng,cthr),rand(1)*.3,mq29);
mq30=if(above(chng,cthr),rand(1)*.3,mq30);
monitor=chng;
q21=mq21;q22=mq22;q23=mq23;q24=mq24;q25=mq25;q26=mq26;
q27=mq27;q28=mq28;q29=mq29;q30=mq30;

monitor=mq1;
decay=.1;
wave_a=0;
zoom=1;
warp=0;

q5=aspectx;
q6=aspecty;


bt=bt+bass_att/fps;
q1=bt;

warp:
shader_body
{
    // sample previous frame
    ret =texture( sampler_fc_main, uv ).xyz;
    vec3 ret2 = texture( sampler_fc_main, uv ).xyz;

    ret.x = texture( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = texture( sampler_main, (uv-0.5)*0.9+0.5 ).y;
    ret.z = texture( sampler_main, (uv-0.5)*1.15+0.5 ).z;   

    // darken (decay) over time
    ret *= 0.75; //or try: ret -= 0.004;
vec3 noise9 = texture(sampler_noisevol_hq, 
                      ((uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*q29
                     ).xyz;
if(ret.x> q21*roam_cos.y && ret.x<= q24*roam_sin.x)ret.y += (noise9).x*.5; 
if(ret.y> q22*roam_sin.x && ret.y<= q25*roam_cos.x)ret.z += (noise9).y*.5; 
if(ret.z> q23*roam_cos.x && ret.z<= q26*roam_sin.y)ret.x += (noise9).z*.5; 
}

comp:
float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body
{
    vec2 d = texsize.zw;
    vec3 dx = ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );
    vec3 dy = ( texture(sampler_main, uv+vec2(0,1)*d).xyz-texture(sampler_main, uv-vec2(0,1)*d).xyz );
vec2 dz = vec2(dx.y,dy.y)*3;

d = texsize.zw*2;
dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

dz +=vec2(dx.y,dy.y);

vec3 bg = _pow(length(dz)*0.8+0,0.7)*2 + ((texture(sampler_blur1, uv).xyz * scale1) + bias1).y*2;

ret =bg*vec3(0.2,0.3,0.6);
vec2 uv_swimmer =fract(0.5 + (uv-0.5)+vec2(q19,q20)*vec2(-1,1)*0);
ret =mix(ret,vec3(3,2,1),((texture(sampler_blur3, uv_swimmer -vec2(dx.y,dy.y)).xyz * scale3) + bias3).x);
ret =mix(ret,0,((texture(sampler_blur2, uv_swimmer-dz).xyz * scale2) + bias2).x);
ret =mix(ret,vec3(0,0,0),1.5*texture(sampler_main, uv_swimmer).xyz.x);

vec3 noise9 = texture(sampler_noisevol_hq, 
                      ((vec3(dx.z,dy.x,uv.y)*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*q30 
                     ).xyz;
if(ret.x> q26*roam_cos.y && ret.x<= q23*roam_sin.x)ret.z -= (noise9).x*.5; 
if(ret.y> q25*roam_sin.x && ret.y<= q22*roam_cos.x)ret.x -= (noise9).y*.5; 
if(ret.z> q24*roam_cos.x && ret.z<= q21*roam_sin.y)ret.y -= (noise9).z*.5; 
}

