comp_hlsl:
shader_body
{
    float2 d = texsize.zw;
    float3 dx = ( GetPixel(uv+float2(1,0)*d)-GetPixel(uv-float2(1,0)*d) );
    float3 dy = ( GetPixel(uv+float2(0,1)*d)-GetPixel(uv-float2(0,1)*d) );
float2 dz = float2(dx.y,dy.y)*3;

d = texsize.zw*2;
dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) );
