frame_eqs_eel:
wave_r = wave_r + 0.25*sin(1.4*time) + 0.25*sin(2.25*time);
wave_g = wave_g + 0.25*sin(1.7*time) + 0.25*sin(2.11*time);
wave_b = wave_b + 0.25*sin(1.84*time) + 0.25*sin(2.3*time);
warp = 0;

decay = decay + abs(0.01*cos(time));

pixel_eqs_eel:
thresh = above(bass_att,thresh)*2+(1-above(bass_att,thresh))*((thresh-1.3)*0.96+1.3);
dx_r = equal(thresh,2)*0.05*sin(5*time)+(1-equal(thresh,2))*dx_r;
dy_r = equal(thresh,2)*0.05*sin(6*time)+(1-equal(thresh,2))*dy_r;

dy = dy - 2*dy_r*(x)*sin(5*sin((8*time)*0.5-x));

dx = dx + abs(dx_r);

warp:
shader_body
{ret=texture(sampler_main, uv).xyz;
vec2 mo=uv-uv_orig;
ret=texture(sampler_main, uv_orig+mo*lum(ret)).xyz;
ret=ret*0.99-0.002;
}

comp:
shader_body
{

ret=((texture(sampler_blur1, uv).xyz * scale1) + bias1);
ret+=((texture(sampler_blur1, vec2(uv.x+ret.x,uv.y+ret.y)).xyz * scale1) + bias1);
ret+=((texture(sampler_blur1, vec2(uv.x-ret.y,uv.y-ret.y)).xyz * scale1) + bias1);
vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((uv.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*q31
                     ).xyz;
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret=pow(length(ret)*ret*1.63,0.99-((texture(sampler_blur3, uv).xyz * scale3) + bias3));
ret=clamp(ret, 0.0, 1.0);
//ret*=texture(sampler_noise_lq,uv*ret.y).xyz;
ret*=ret.z;
ret=ret-texture(sampler_main, uv).xyz;
//ret -= 0.1;
ret /= vec3(.8,1.0,0.9);
//MilkDrop3 Color Mode:
ret /= vec3(1.0,-2.0,2.0);
}

comp_hlsl:
shader_body
{

ret=GetBlur1(uv);
ret+=GetBlur1(float2(uv.x+ret.x,uv.y+ret.y));
ret+=GetBlur1(float2(uv.x-ret.y,uv.y-ret.y));
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q31
                     );
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret=pow(length(ret)*ret*1.63,0.99-GetBlur3(uv));
ret=saturate(ret);
//ret*=tex2D(sampler_noise_lq,uv*ret.y);
ret*=ret.z;
ret=ret-GetPixel(uv);
//ret -= 0.1;
ret /= float3(.8,1.0,0.9);
//MilkDrop3 Color Mode:
ret /= float3(1.0,-2.0,2.0);
}
