frame_eqs_eel:
vol=(bass+mid+treb)*0.25;
vol=vol;
q3=vol;
mtime = mtime+vol*0.01;
q1=mtime;

zoom = zoom + 0.015*vol;

warp:
uniform sampler2D sampler_fw_noisevol_hq;
shader_body
{

    vec3 pos = vec3(uv.x, uv.y, q1);
    vec3 rc = texture(sampler_fw_noisevol_hq, pos).xyz;
    rc += texture(sampler_fw_noisevol_hq, 2*pos).xyz*0.5;
    rc += texture(sampler_fw_noisevol_hq, 4*pos).xyz*0.25;
    rc += texture(sampler_fw_noisevol_hq, 8*pos).xyz*0.125;
    rc += texture(sampler_fw_noisevol_hq, 16*pos).xyz*0.0625;
    //rc = rc*0.556;    
    rc = (rc*rc*rc);
    rc -= ((texture(sampler_blur1, uv).xyz * scale1) + bias1);   
    rc *= q3;

    vec2 ruv = uv = 0.5 + (uv-0.5)*(1-(rc.x*.05));  
    ret = texture(sampler_fc_main, vec2(ruv.x, ruv.y)).xyz;

    vec3 noise2 = texture(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy).xyz;
    noise2 += mix(rc*2, 2, rad);

    ret = ret+0.5*lum(cross((1-ret),(noise2)));

    ret *= 0.98;

}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;
    ret = 1-ret;    

    ret *= 1.14; //gamma
    ret *= ret; //darken
}

warp_hlsl:
sampler sampler_fw_noisevol_hq;
shader_body
{

    float3 pos = float3(uv.x, uv.y, q1);
    float3 rc = tex3D(sampler_fw_noisevol_hq, pos);
    rc += tex3D(sampler_fw_noisevol_hq, 2*pos)*0.5;
    rc += tex3D(sampler_fw_noisevol_hq, 4*pos)*0.25;
    rc += tex3D(sampler_fw_noisevol_hq, 8*pos)*0.125;
    rc += tex3D(sampler_fw_noisevol_hq, 16*pos)*0.0625;
    //rc = rc*0.556;    
    rc = (rc*rc*rc);
    rc -= GetBlur1(uv);   
    rc *= q3;

    float2 ruv = uv = 0.5 + (uv-0.5)*(1-(rc.x*.05));  
    ret = tex2D(sampler_fc_main, float2(ruv.x, ruv.y)).xyz;

    float3 noise2 = tex2D(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy);
    noise2 += lerp(rc*2, 2, rad);

    ret = ret+0.5*lum(cross((1-ret),(noise2)));

    ret *= 0.98;

}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret = 1-ret;    

    ret *= 1.14; //gamma
    ret *= ret; //darken
}
