ob_a = 0;
t = -1000;
count = 0;
avg = 0;

frame_eqs_eel:
dec_med = dec_med*0.9 + min(1,max(0, bass_att-1))*0.02;
dec_hard = dec_hard*0.95 + min(1,max(0, treb_att-1))*0.01;
med = med - dec_med*2;
hard = hard - dec_hard*2;
vol = (med+hard)*0.5;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
q1 = vol;
q2 = bass_thresh;
mtime = mtime+vol*0.002;
q3 = mtime;
count = count+1;
avg = avg*0.95 + vol*0.05;
q4 = avg;
monitor = q4;
t = if(above(avg,0.7),t+min(vol,0.04),t);
//t = t + max(min(vol*2,0.02),-0.02);
