warp:
//Shader from diatribes
//https://www.shadertoy.com/view/3cjcWD
//Ported to MilkDrop by MilkDrop2077

shader_body
{
    vec4 col = 0;
    vec2 coord = (uv * 2) - 1;       
    coord *= aspect.xy;                
    coord = vec2(coord.x, -coord.y); 
    float dist, angle, temp, stepVal;
    float iter = 0;
    float t = time * 0.5;
    vec3 entityPos, pos = vec3(uv.x, uv.y, 0);

    coord += vec2(cos(t * 0.4) * 0.3, cos(t * 0.8) * 0.1);

    for (col *= iter; iter++ < 80;
         dist += stepVal = min(0.01 + 0.8 * abs(stepVal), temp = max(0.8 * temp, 0.01)),
         col += 1.0 / (stepVal + temp * 2.0))

        for (pos = vec3(coord * dist, dist + t),
             entityPos = pos - vec3(
                 sin(sin(t) + t * 0.4+q1/6) * 8.0,
                 sin(sin(t) + t * 0.2) * 2.0,
                 16.0 + t + cos(t) * 8.0),

             temp = 2.0 * length(entityPos) + 0.2,
             stepVal = mix(temp * 0.02, 4.0 + pos.y, smoothstep(0.0, 12.0, length(entityPos))),

             angle = 0.4; angle < 8.0; angle *= 1.4)

            stepVal -= abs(dot(cos(t + 0.2 * pos.z + pos * angle), 0.11 + pos - pos)) / angle;

    col = tanh(vec4(1, 2, 5, 0) * col / 1e1 / length(coord - 0.65))*q2*0.6;
    ret = col;
}

