}

comp:
shader_body
{
    ret =texture(sampler_main, uv).xyz*1 +
              ((texture(sampler_blur1, uv).xyz * scale1) + bias1)*3 +
              ((texture(sampler_blur2, uv).xyz * scale2) + bias2)*0 +
              ((texture(sampler_blur3, uv).xyz * scale3) + bias3)*0 +
              0;
    ret += ((texture(sampler_blur2,  (uv-0.5)*0.333 + 0.5).xyz * scale2) + bias2);
    ret *= 1;
}

warp_hlsl:
