frame_eqs_eel:
dt=1/fps; dec_m = 1-2*dt; dec_s = 1-0.5*dt; 
vol = (2*bass_att+mid_att+treb_att)/3;

p = progress;
trest = if (p==0,100000, (time-tstart)*(1-p)/p);

v = max(0,vol);
dec = if (v>vol_,1-1*dt,1-0.25*dt);
vol_= vol_*dec+(1-dec)*v;
spd = vol_*dt * (0.5+zo__)/4;

isb = above(vol, pk) * (time>t0+.5);
if (isb,t0=time; pk=vol*3,Pk*=dec_s);
bi += isb;

sw = isb&&(rand(1)>0.7)&&(time-ts>8);
if (sw,ts=time,0);
zo = 2;
zo_ += 2;
zo__ = 2;


if (isb&&(bi%2==0),r1 = rand(2)-1,0);
if (isb&&(bi%3==0),r2 = rand(2)-1,0);
if (isb&&(bi%5==2),r3 = rand(12)-1,0);

r1a += atan(150*(r1-r1a))*spd*.01;
r2a += atan(150*(r2-r2a))*spd*.01;
r3a += atan(150*(r3-r3a))*spd*.01;

r1b = r1b*dec_m + (1-dec_m)*r1a;
r2b = r2b*dec_m + (1-dec_m)*r2a;
r3b = r3b*dec_m + (1-dec_m)*r3a;
r1c = r1c*dec_m + (1-dec_m)*r1b;
r2c = r2c*dec_m + (1-dec_m)*r2b;
r3c = r3c*dec_m + (1-dec_m)*r3b;

s1 += (1+r1c)*spd;
s2 += (r2c)*spd;
s3 += (1+r2c)*spd;

q1=r1c;
q2=r2c;
q3=r3c;
q4=(q1-q2)*0.2;
q5=s1;
q6=s2;
q7=s3*4;
q8=(s2+s3)*12;
q10 = zo__;

q12 = 0.2; //front-back light
tr = tr*dec_m + (1-dec_m)*trest/5;
q20 = max(0,min(1,tr)); //fade - does not work

//colors
t1 += spd*0.12;
t2 += spd*0.07;
q21 = 1.5 + 0.3*sin(t1);
q22 = 1 + 0.3*sin(t2); 
monitor = t1;

