uniform sampler2D sampler_pw_noise_lq;
float dr, smask, stripe, h, dist;
vec2 uv1, uv2, uv3,uv4,uv5,uv6;
vec3 lea, noise, ret1, struc, neu;

float aTan2(float y, float x) {float angle;	 float abs_y = abs(y); float r; 
if (x>=0) {r=1-(x-abs_y) / (x+abs_y); } else {r=3-(x+abs_y) / (abs_y-x);}	
return (y < 0 ? -r : r)/4;}

shader_body {
vec2 uv1 = (uv-.5+0.2*q2)*aspect.xy;
lea = 0;
int anz = 16;
for (float n=anz; n>0; n--) {
  dist = (3.5+q3)*0.33*n/anz;
  uv4 = 256 * (dist* uv1)*q10;

  float aa = (4+q3/2)*n/anz+q1*(1+uv1.x*0)/2 ;
  uv4 = (mat2(cos(aa),-sin(aa),sin(aa),cos(aa)) * uv4);
  stripe = (26-0.8*abs(floor(uv4.x)));;
  uv4.y += stripe*q5/5 + (16-stripe)*q6 - 8.0*n;

  noise = texture (sampler_pw_noise_lq,uv4/256).xyz;
  smask = sat (64*(noise.b-(0.98-(stripe/16)*0.1))); //0.1=dens.

  aa =  (noise.g-0.5)*q8;
  uv5 = (mat2(cos(aa),-sin(aa),sin(aa),cos(aa)) * fract(uv4)-0.5);

  dr = (abs(sin(uv4.y*0.5 +2*q7*noise.r)));
  uv5.x *= 1/dr;
  struc = texture(sampler_noise_hq,0.5*aTan2(uv5.x,uv5.y+0.4)).xyz-0.5;
  uv6 = uv5;
  uv5 += struc*n*.25*(noise.r);
  h = smask*sat (8-16*length(uv5));
  neu = h*vec3 ((noise.rg+vec2(q21,q22))*(.16/abs(dr-struc.x)),0.9);
  lea = mix (lea, neu, h);
}
