vec3 noise, ret1, ret2, neu, mus;

float matan (float x)
{return x*(1.061 - abs(x)*0.274);}

float matan2 (float im, float re)
{float x;
   if (abs(re) > abs(im)) 
   {x = matan (im/re); 
    if (re<0) {x += M_PI;}
   }
   else
   {x = M_PI/2 -matan (re/im); 
   if (im<0) {x += M_PI;}
   } 
  return (x>=M_PI? x-2*M_PI: x);
}


float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body {//MilkDrop3 Zoom Mode:
uv = 0.25 + 0.5*uv;

uv10 = (uv-.5)*aspect.xy; 
float ang1 = matan2 (uv10.x,uv10.y)/M_PI;
float ang2 = 0.5 + (1.0 - abs( fract(ang1 * 0.5) * 2 - 1.0 )-.5); 
float z = 0.02;
uv1 = vec2 (.6+3*z-z/length (uv10), ang2).yx;
ret1= ret2= 0;
dz= dz1=0;
uv3 = uv1;
float mask = clamp(4*uv3.y, 0.0, 1.0) * clamp(rad*1, 0.0, 1.0);
uv3 = fract(uv3);
dz0.x = (lum(GP(uv3+hor))- lum(GP(uv3-hor)));
dz0.y = (lum(GP(uv3+ver))- lum(GP(uv3-ver)));
dz0.x += 2*(lum(GB(uv3+hor))- lum(GB(uv3-hor)));
dz0.y += 2*(lum(GB(uv3+ver))- lum(GB(uv3-ver)));
dz +=  mask*dz0/2;
ret1 = max(ret1,(GP(uv3)+GB(uv3))/2*mask);
ret2 = abs(texture(sampler_main, uv3).xyz-((texture(sampler_blur1, uv3).xyz * scale1) + bias1));

mus = 1.2/length(sin(12*(uv1+dz/2 + 2.25*(ret1-0.5)) +time))*rad*(1-rad);

float stars = 0;
int anz = 6;
uv2 = vec2 (0.02/length (uv10), ang2).yx;
for (int n=1; n <= anz; n++) 
{
  vec2 uv4 = 8.0*((1.0+n)*(uv2+(dz+(ret1-.5)) + 0.1*n )+ vec2(0,time*.1));
  float smask = clamp(texture (sampler_pw_noise_lq,uv4/256).xyz-.95, 0.0, 1.0);
  stars = max(stars,(0.1/_pow(length(fract(uv4)-.5),2)*smask));
n++;
}

//mus = max(mus,stars);
uv2 = vec2 (22*length (uv10), ang2).yx;
uv2 = uv2*.2+vec2(0.07*time,.5);
uv2=fract(uv2);
vec3 bk = ((texture(sampler_blur2, uv2).xyz * scale2) + bias2) * 2*(1-rad) + .2;
ret =sqrt(ret1)*(1+ret2)*(.25+mus);
ret *= 1+6*length(dz);
ret += clamp(stars, 0.0, 1.0)*.7;
ret += 2*bk* clamp(1-lum(ret1)*16, 0.0, 1.0)*sqrt(mid_att+1)*clamp(0.035/(0.001+sqrt(length(uv10))), 0.0, 1.0);

ret *= 3.7-rad;
ret -= 0.3;
//ret =texture(sampler_main, uv).xyz;
//MilkDrop3 Color Mode:
ret /= vec3(2.7,1.6,1.6);
}

